final.floor <- function(instructions) {
    currentFloor <- 0
    for(char in 1:nchar(instructions)){
        curr_char = substr(instructions,char,char)
        if(curr_char == "("){
            currentFloor <- currentFloor+1
        }else{
            currentFloor <- currentFloor-1
        }
    }
   return(currentFloor)
}


stopifnot(
    final.floor("(())") == 0,
    final.floor("()()") == 0,
    final.floor("(((") == 3
)
final.floor(input)


basement.pos <- function(instructions) {
    currentFloor <- 0
    for(char in 1:nchar(instructions)){
        curr_char = substr(instructions,char,char)
        if(curr_char == "("){
            currentFloor <- currentFloor+1
        }else{
            currentFloor <- currentFloor-1
        }
        if(currentFloor < 0){
            return(char)
        }
    }
}

stopifnot(
    basement.pos(")") == 1,
    basement.pos("()())") == 5
)
basement.pos(input)
